import React, { createContext, useState, useContext, ReactNode } from 'react';
import Reservation from '../interfaces/Reservation';

interface ReservationContextType {
    reservations: Reservation[];
    addReservation: (reservation: Reservation) => void;
    removeReservation: (id: string) => void;
}

const ReservationContext = createContext<ReservationContextType | undefined>(undefined);

export function useReservationContext() {
    const context = useContext(ReservationContext);
    if (!context) {
        throw new Error('useReservationContext must be used within a ReservationProvider');
    }
    return context;
}

interface ReservationProviderProps {
    children: ReactNode;
}

export function ReservationProvider({ children }: ReservationProviderProps) {
    const [reservations, setReservations] = useState<Reservation[]>([
        {
            "id": "1697738736749",
            "customerName": "Vincent",
            "phoneNumber": 564400004,
            "guestCount": 2,
            "tableNumber": 1,
            "note": "I am vegetarian"
        },
        {
            "id": "1697738757842",
            "customerName": "Cloud",
            "phoneNumber": 9055011400,
            "guestCount": 6,
            "tableNumber": 7,
            "note": "Coming with group of friends, one has peanut allergy"
        },
        {
            "id": "1697738757842",
            "customerName": "Sephiroth",
            "phoneNumber": 1014400444,
            "guestCount": 1,
            "tableNumber": 6,
            "note": "Get me the best wine you have"
        },
        {
            "id": "1697738757842",
            "customerName": "Squall Leonhart",
            "phoneNumber": 5011400,
            "guestCount": 2,
            "tableNumber": 10,
            "note": "It's going to be a date night, so please prepare roses on the table"
        }
    ]);
    const addReservation = (reservation: Reservation) => {
        setReservations([...reservations, reservation]);
    };

    const removeReservation = (id: string) => {
        setReservations(reservations.filter((res) => res.id !== id));
    };

    return (
        <ReservationContext.Provider value={{ reservations, addReservation, removeReservation }}>
            {children}
        </ReservationContext.Provider>
    );

};
