import React, { useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import { useReservationContext } from "../store/ReservationContext";
import '../App.css';
import Reservation from "../interfaces/Reservation";

const AddReservation = () => {
    const navigate = useNavigate();
    const { addReservation } = useReservationContext();

    const [formData, setFormData] = useState<Reservation>({
        id: new Date().getTime().toString(),
        customerName: '',
        phoneNumber: 0,
        guestCount: 0,
        tableNumber: 0,
        note: '',
        isCompleted: false
    });

    const handleChange = (e: React.ChangeEvent<HTMLInputElement | HTMLTextAreaElement>) => {
        const { name, value } = e.target;
        setFormData({
            ...formData,
            [name]: value
        });
    };

    const handleSubmit = (e: React.FormEvent<HTMLFormElement>) => {
        e.preventDefault();
        addReservation(formData);
        navigate('/');
    };

    return (
        <>
            <nav className="App-nav">
                <Link to="/">Back</Link>
                <h1 className="App-nav-h1">Add Reservation</h1>
                <span>X</span>
            </nav>
            <div className="container">
                <form onSubmit={handleSubmit}>
                    <div>
                        <label>Customer Name</label>
                        <input type="text" name="customerName" required onChange={handleChange} />
                    </div>
                    <div>
                        <label>Phone Number</label>
                        <input type="tel" name="phoneNumber" required onChange={handleChange} />
                    </div>
                    <div>
                        <label>Guest Count</label>
                        <input type="number" name="guestCount" onChange={handleChange} />
                    </div>
                    <div>
                        <label>Table Number</label>
                        <input type="number" name="tableNumber" onChange={handleChange} />
                    </div>
                    <div>
                        <label>Note</label>
                        <textarea name="note" onChange={handleChange}></textarea>
                    </div>
                    <button className="btn-confirm" type="submit">
                        Add Reservation
                    </button>
                </form>
            </div>
        </>
    )
};

export default AddReservation;
