import { Link, useNavigate, useParams } from "react-router-dom";
import Reservation from "../interfaces/Reservation";
import '../App.css';
import { useReservationContext } from "../store/ReservationContext";
import { useEffect, useState } from "react";

const EditReservation = () => {
    const { id } = useParams();
    const navigate = useNavigate();
    const { reservations, editReservation } = useReservationContext();

    const [formData, setFormData] = useState<Reservation | undefined>(reservations.find((res) => res.id === id));

    useEffect(() => {
        if (!formData) {
            navigate('/');
        }
    }, [formData, navigate]);

    const handleEdit = () => {
        if (!formData) {
            // Handle the case when formData is undefined
            return;
        }

        // Call the editReservation function from the context to edit the reservation
        editReservation(formData as Reservation);

        navigate('/'); // Navigate back to the Dashboard after editing
    };

    const handleChanges = (e: React.ChangeEvent<HTMLInputElement | HTMLTextAreaElement>) => {
        setFormData((prevData) => {
            return {
                ...(prevData as Reservation),
                [e.target.name]:
                    e.target.name === 'phoneNumber' || e.target.name === 'guestCount' || e.target.name === 'tableNumber'
                        ? parseInt(e.target.value)
                        : e.target.value,
            };
        });
    };

    return (
        <>
            <nav className="App-nav">
                <Link to="/">Back</Link>
                <h1>Edit Reservation</h1>
                <span>X</span>
            </nav>
            {formData ? (
                <form>
                    <div>
                        <label>Customer Name</label>
                        <input type="text" name="customerName" value={formData.customerName} onChange={handleChanges} />
                    </div>
                    <div>
                        <label>Phone Number</label>
                        <input type="tel" name="phoneNumber" value={formData.phoneNumber.toString()} onChange={handleChanges} />
                    </div>
                    <div>
                        <label>Guest Count</label>
                        <input type="number" name="guestCount" value={formData.guestCount.toString()} onChange={handleChanges} />
                    </div>
                    <div>
                        <label>Table Number</label>
                        <input type="number" name="tableNumber" value={formData.tableNumber.toString()} onChange={handleChanges} />
                    </div>
                    <div>
                        <label>Note</label>
                        <textarea name="note" value={formData.note} onChange={handleChanges}></textarea>
                    </div>
                    <button type="button" onClick={handleEdit}>
                        Save Changes
                    </button>
                </form>
            ) : null}
        </>
    )
};

export default EditReservation;
