import { Link } from "react-router-dom";
import Reservation from "../interfaces/Reservation";
import { useReservationContext } from "../store/ReservationContext";
import '../App.css';

const Dashboard = () => {
    const { reservations } = useReservationContext();
    const { removeReservation } = useReservationContext();

    const handleRemoveReservation = (id: string) => {
        removeReservation(id);
    };

    return (
        <>

            <nav className="App-nav">
                <Link to="/addReservation" className="addBtn">+ Add Reservation</Link>
                <h1>Reservation</h1>
                <span>X</span>
            </nav>
            <ul className="App-reservation-ul">
                {reservations.map((reservation: Reservation) => (
                    <li key={reservation.id}>
                        <Link to={`/editReservation/${reservation.id}`}>
                        <p>Customer Name: {reservation.customerName}</p>
                        <p>Phone Number: {reservation.phoneNumber}</p>
                        <p>Guest Count: {reservation.guestCount}</p>
                        <p>Table Number: {reservation.tableNumber}</p>
                        <p>Note: {reservation.note}</p>
                        <button onClick={() => handleRemoveReservation(reservation.id)}>Remove reservation</button>
                        </Link>
                        
                    </li>
                ))}
            </ul>
        </>
    )
};

export default Dashboard;
