import { Link } from "react-router-dom";
import Reservation from "../interfaces/Reservation";
import { useReservationContext } from "../store/ReservationContext";
import '../App.css';

const Dashboard = () => {
    const { reservations, removeReservation, editReservation } = useReservationContext();

    console.log(reservations);

    const handleRemoveReservation = (id: string, e: React.MouseEvent<HTMLButtonElement>) => {
        e.preventDefault();
        removeReservation(id);
    };

    const handleFinishedReservation = (id: string) => {
        // Find the reservation to be marked as completed
        const reservationToMarkAsCompleted = reservations.find((reservation) => reservation.id === id);

        if (!reservationToMarkAsCompleted) {
            // Handle the case when the reservation is not found
            return;
        }

        // Update the isCompleted property of the reservation
        reservationToMarkAsCompleted.isCompleted = true;

        // Call the editReservation function from the context to update the reservation
        editReservation(reservationToMarkAsCompleted);
    };

    const activeReservations = reservations.filter((reservation) => !reservation.isCompleted);

    return (
        <>

            <nav className="App-nav">
                <Link to="/addReservation" className="addBtn">+ Add Reservation</Link>
                <h1 className="App-nav-h1">Reservation <span className="App-nav-reservationCounter">{activeReservations.length}</span></h1>
                <span>X</span>
            </nav>
            <div className="container px-4">
                <ul className="row gx-3 App-reservation-ul">
                    {activeReservations.map((reservation: Reservation) => (
                        <li className="col-4 App-reservation-li" key={reservation.id}>
                            <Link to={`/editReservation/${reservation.id}`}>
                                <div className="App-reservation-li-content">
                                    <div className="d-flex justify-content-start align-items-center">
                                        <p className="me-3">{reservation.customerName}</p>
                                        <p className="App-reservation-phone">{reservation.phoneNumber}</p>
                                    </div>
                                    <p>Guest Count: {reservation.guestCount}</p>
                                    <p>Reserved table {reservation.tableNumber}</p>
                                    <p>Note: {reservation.note}</p>
                                    <div className="d-flex justify-content-between mt-auto">
                                        <button className="btn btn-primary" onClick={(e) => handleRemoveReservation(reservation.id, e)}>Remove reservation</button>
                                        <button className="btn btn-primary" onClick={(e) => { e.preventDefault(); handleFinishedReservation(reservation.id); }}>
                                            Seated
                                        </button>
                                    </div>
                                </div>
                            </Link>
                        </li>
                    ))}
                </ul>
            </div>
        </>
    )
};

export default Dashboard;
