import React, { useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import { useReservationContext } from "../store/ReservationContext";
import '../App.css';
import Reservation from "../interfaces/Reservation";
import styled from 'styled-components';
import Select from "react-select";
import tableOptions from "../interfaces/TableOptions";

const AddReservationWrapper = styled.div`
  background-color: #fff;
`;



const AddReservation = () => {
    const navigate = useNavigate();
    const { addReservation } = useReservationContext();

    const [formData, setFormData] = useState<Reservation>({
        id: new Date().getTime().toString(),
        customerName: '',
        phoneNumber: 0,
        guestCount: 0,
        tableNumber: 0,
        note: '',
        isCompleted: false
    });

    const handleChange = (e: React.ChangeEvent<HTMLInputElement | HTMLTextAreaElement>) => {
        const { name, value } = e.target;
        setFormData({
            ...formData,
            [name]: value
        });
    };

    const handleSubmit = (e: React.FormEvent<HTMLFormElement>) => {
        e.preventDefault();
        addReservation(formData);
        navigate('/');
    };

    const increaseCount = (e: React.MouseEvent<HTMLButtonElement>) => {
        e.preventDefault();
        setFormData({
            ...formData,
            guestCount: formData.guestCount + 1
        });
    };

    const decreaseCount = (e: React.MouseEvent<HTMLButtonElement>) => {
        e.preventDefault();
        setFormData({
            ...formData,
            guestCount: formData.guestCount - 1
        });
    };

    return (
        <AddReservationWrapper>
            <nav className="App-nav">
                <Link to="/">Back</Link>
                <h1 className="App-nav-h1">Add Reservation</h1>
                <span>X</span>
            </nav>
            <div className="container">
                <form onSubmit={handleSubmit}>
                    <div className="my-3 d-flex justify-content-between align-items-center">
                        <div>
                            <input className="text-inputField" placeholder="Name" type="text" name="customerName" required onChange={handleChange} />
                        </div>
                        <div>
                            <input className="text-inputField" placeholder="Phone" type="tel" name="phoneNumber" required onChange={handleChange} />
                        </div>
                    </div>
                    <div className="my-3 d-flex align-items-center justify-content-between">
                        <div className="d-flex align-items-center justify-content-between">
                            <label className="me-3 fw-bold">Guests</label>
                            <button className="btn btn-count" onClick={decreaseCount}>-</button>
                            <input readOnly={true} className="mx-3 guestCountInput" type="number" name="guestCount" value={formData.guestCount} onChange={handleChange} />
                            <button className="btn btn-count" onClick={increaseCount}>+</button>
                        </div>
                        <div>
                            <Select
                                isMulti
                                name="colors"
                                options={tableOptions}
                                className="basic-multi-select"
                                classNamePrefix="select"
                            />
                            {/* <label>Table Number</label>
                            <input type="number" name="tableNumber" onChange={handleChange} /> */}
                        </div>
                    </div>

                    <div className="mt-3 mb-5">
                        <textarea rows={5} className="w-100" placeholder="Add Note..." name="note" onChange={handleChange}></textarea>
                    </div>
                    <button className="btn btn-confirm" type="submit">
                        Save
                    </button>
                </form>
            </div>
        </AddReservationWrapper>
    )
};

export default AddReservation;
