import { Link, useNavigate, useParams } from "react-router-dom";
import Reservation from "../interfaces/Reservation";
import '../App.css';
import { useReservationContext } from "../store/ReservationContext";
import { useEffect, useState } from "react";

const EditReservation = () => {
    const { id } = useParams();
    const navigate = useNavigate();
    const { reservations, addReservation } = useReservationContext();

    const [formData, setFormData] = useState<Reservation | undefined>(reservations.find((res) => res.id === id));

    useEffect(() => {
        if (!formData) {
            navigate('/');
        }
    }, [formData, navigate]);

    const handleEdit = () => {
        addReservation(formData as Reservation);
        navigate('/');
    };

    const handleCustomerNameChange = (e: React.ChangeEvent<HTMLInputElement | HTMLTextAreaElement>) => {
        setFormData({
            ...formData,
            customerName: e.target.value,
            phoneNumber: e.target.value,
            guestCount: e.target.value,
            tableNumber: e.target.value,
            note: e.target.value
        });
    };

    return (
        <>
            <nav className="App-nav">
                <Link to="/">Back</Link>
                <h1>Edit Reservation</h1>
                <span>X</span>
            </nav>
            {formData ? (
                <form>
                    <div>
                        <label>Customer Name</label>
                        <input type="text" name="customerName" value={formData.customerName} onChange={handleCustomerNameChange}
                        />
                    </div>
                    <div>
                        <label>Phone Number</label>
                        <input type="tel" name="phoneNumber" value={formData.phoneNumber} onChange={handleCustomerNameChange} />
                    </div>
                    <div>
                        <label>Guest Count</label>
                        <input type="number" name="guestCount" value={formData.guestCount} onChange={handleCustomerNameChange} />
                    </div>
                    <div>
                        <label>Table Number</label>
                        <input type="number" name="tableNumber" value={formData.tableNumber} onChange={handleCustomerNameChange} />
                    </div>
                    <div>
                        <label>Note</label>
                        <textarea name="note" value={formData.note} onChange={handleCustomerNameChange}></textarea>
                    </div>
                    <button type="button" onClick={handleEdit}>
                        Save Changes
                    </button>
                </form>
            ) : null}
        </>
    )
};

export default EditReservation;
