import React, { useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import { useReservationContext } from "../store/ReservationContext";
import '../App.css';
import Reservation from "../interfaces/Reservation";
import styled from 'styled-components';
import Select, { ActionMeta, MultiValue } from "react-select";
import tableOptions from "../interfaces/TableOptions";
import DateTimePicker from 'react-datetime-picker';
import 'react-datetime-picker/dist/DateTimePicker.css';
import 'react-calendar/dist/Calendar.css';
import 'react-clock/dist/Clock.css';

const AddReservationWrapper = styled.div`
  background-color: #fff;
`;

type ValuePiece = Date | null;

type Value = ValuePiece | [ValuePiece, ValuePiece];

const AddReservation = () => {
    const navigate = useNavigate();
    const { addReservation } = useReservationContext();
    const [value, onChange] = useState<Value>(new Date());

    const [formData, setFormData] = useState<Reservation>({
        id: new Date().getTime().toString(),
        customerName: '',
        phoneNumber: 0,
        guestCount: 0,
        tableNumbers: [],
        note: '',
        isCompleted: false,
        reservationTime: null
    });

    const handleSubmit = (e: React.FormEvent<HTMLFormElement>) => {
        e.preventDefault();
        const newReservation = {
            id: formData.id,
            customerName: formData.customerName,
            phoneNumber: formData.phoneNumber,
            guestCount: formData.guestCount,
            tableNumbers: formData.tableNumbers,
            note: formData.note,
            isCompleted: false,
            reservationTime: value as Date
        };
        addReservation(newReservation);
        navigate('/');
    };

    const increaseCount = (e: React.MouseEvent<HTMLButtonElement>) => {
        e.preventDefault();
        setFormData({
            ...formData,
            guestCount: formData.guestCount + 1
        });
    };

    const decreaseCount = (e: React.MouseEvent<HTMLButtonElement>) => {
        e.preventDefault();
        setFormData({
            ...formData,
            guestCount: formData.guestCount - 1
        });
    };

    const handleCustomerNameChange = (e: React.ChangeEvent<HTMLInputElement>) => {
        const { name, value } = e.target;
        setFormData({ ...formData, [name]: value });
    };

    const handlePhoneNumberChange = (e: React.ChangeEvent<HTMLInputElement>) => {
        const { name, value } = e.target;
        setFormData({ ...formData, [name]: value });
    };

    const handleGuestCountChange = (e: React.ChangeEvent<HTMLInputElement>) => {
        const { name, value } = e.target;
        setFormData({ ...formData, [name]: value });
    };

    const handleNoteChange = (e: React.ChangeEvent<HTMLTextAreaElement>) => {
        const { name, value } = e.target;
        setFormData({ ...formData, [name]: value });
    };

    const handleTableNumbersChange = (
        selectedOptions: MultiValue<{ value: string; label: string }>,
        actionMeta: ActionMeta<{ value: string; label: string }>
    ) => {
        if (actionMeta.action === 'select-option' || actionMeta.action === 'remove-value') {
            const selectedTableNumbers = selectedOptions.map((option) => option.value);
            setFormData({ ...formData, tableNumbers: selectedTableNumbers });
        }
    };

    const handleDateTimeChange = (value: Value) => {
        onChange(value as Date); // Ensure the value is of type Date
    };

    return (
        <AddReservationWrapper>
            <nav className="App-nav">
                <Link to="/">Back</Link>
                <h1 className="App-nav-h1">Add Reservation</h1>
                <span>X</span>
            </nav>
            <div className="container">
                <form onSubmit={handleSubmit}>

                    <div className="my-3 d-flex justify-content-between align-items-center">
                        <div>
                            <input className="text-inputField" placeholder="Name" type="text" name="customerName" required onChange={handleCustomerNameChange} />
                        </div>
                        <div>
                            <input className="text-inputField" placeholder="Phone" type="tel" name="phoneNumber" required onChange={handlePhoneNumberChange} />
                        </div>
                        <div className="w-25">
                            <div>
                                <label>Reservation Time</label>
                                <div className="">
                                    <DateTimePicker
                                        onChange={handleDateTimeChange}
                                        value={value}
                                        format="h:mm a"
                                        calendarIcon={null}
                                        clearIcon={null}
                                        disableClock={true}
                                    />
                                </div>
                            </div>
                            <div>
                                <label>Reservation Date</label>
                                <div className="date-picker">
                                    <DateTimePicker
                                        onChange={handleDateTimeChange}
                                        value={value}
                                        format="MM-dd"
                                        clearIcon={null}
                                    />
                                </div>
                            </div>
                            <div>
                                <div className="time-picker">
                                    <label>Reservation Time that you can increment with Plus and Minus buttons for hour and minutes</label>
                                    <DateTimePicker
                                        onChange={handleDateTimeChange}
                                        value={value}
                                        format="h:mm a"
                                        calendarIcon={null}
                                        clearIcon={null}
                                        disableClock={true}
                                    />
                                </div>
                            </div>

                        </div>
                    </div>
                    <div className="my-3 d-flex align-items-center justify-content-between">
                        <div className="d-flex align-items-center justify-content-between">
                            <label className="me-3 fw-bold">Guests</label>
                            <button className="btn btn-count" onClick={decreaseCount}>-</button>
                            <input readOnly={true} className="mx-3 guestCountInput" type="number" name="guestCount" value={formData.guestCount} onChange={handleGuestCountChange} />
                            <button className="btn btn-count" onClick={increaseCount}>+</button>
                        </div>
                        <div className="w-50">
                            <Select
                                isMulti
                                name="tableNumbers"
                                options={tableOptions}
                                placeholder="Select Table"
                                className="basic-multi-select guest-select"
                                classNamePrefix="select"
                                value={tableOptions.filter((option) => formData.tableNumbers.includes(option.value))}
                                onChange={handleTableNumbersChange}
                            />
                        </div>
                    </div>

                    <div className="mt-3 mb-5">
                        <textarea rows={5} className="w-100" placeholder="Add Note..." name="note" onChange={handleNoteChange}></textarea>
                    </div>
                    <button className="btn btn-confirm" type="submit">
                        Save
                    </button>
                </form>
            </div>
        </AddReservationWrapper>
    )
};

export default AddReservation;
